    public static int numSplits(String s) {
        int goodCount = 0;
        for(int i = 0; i < s.length(); i++){
            String leftString = s.substring(0, i + 1);
            String rightString = s.substring(i + 1);
            if(leftString.chars().distinct().count() == rightString.chars().distinct().count()){
                goodCount++;
            }
        }
        return goodCount;
    }

